	if (date.mm<1||date.mm>12)
	{
		system("cls");
			gotoxy(6,12);
			printf("\aInvalid Input!!! There are at most 12 months in a year.");
		gotoxy(6,14);
		printf("Redirecting in 3 seconds...");
		Sleep(3000);
		ask();
	}
	gotoxy(15,12);
	printf("-> Enter the day: ");
